import React, { useState } from "react";
import { useFormik } from "formik";
import * as Yup from "yup";
import { Form, Button } from "react-bootstrap";
import { consultaUno } from "../../api/empresa";
import { toast } from "react-toastify";

export default function ConsultaUno() {
  //variable  de estado para el manejo de error de servidor
  const [error, setError] = useState("");

  const formik = useFormik({
    initialValues: initialValues(),
    validationSchema: Yup.object({
      id: Yup.string().required("el titulo es obligatorio"),
    }),
    onSubmit: async (consultaData) => {
      console.log("consulta:", consultaData);
      setError("");

      const result = await consultaUno(consultaData);

      if (result.message) {
        toast.success("consulta creada");
        console.log(result);
      } else {
        setError(error.message);
        toast.error(error.message);
      }
    },
  });
  return (
    <Form className="login-form" onSubmit={formik.handleSubmit}>
      <h2>Ingrese el id a buscar.</h2>
      <Form.Control
        type="number"
        placeholder="Inserte ID"
        name="id"
        value={formik.values.id}
        onChange={formik.handleChange}
        error={formik.errors.id && true}
      />
      <Button type="submit" className="btn-submit">
        Confirmar
      </Button>
      {error && <p className="submit-error">{error}</p>}
    </Form>
  );
}

function initialValues() {
  return {
    id: "",
  };
}
