import React, { useState } from "react";
import { useFormik } from "formik";
import * as Yup from "yup";
import { Form, Button } from "react-bootstrap";
import { consultaUno } from "../../api/consultas";
import { toast } from "react-toastify";

export default function ConsultaUno() {
  //variable  de estado para el manejo de error de servidor
  const [error, setError] = useState("");

  const formik = useFormik({
    initialValues: initialValues(),
    validationSchema: Yup.object({
      id: Yup.string().required("El ID es obligatorio"),
    }),
    onSubmit: async (id) => {
      console.log("id:", id.id);
      setError("");

      const result = await consultaUno(id);

      const [consulta, setConsulta] = useState([]);
      //seteo de recarga de libros
      const [reloadConsulta, setReloadConsulta] = useState(false);

      // efecto que retorna los datos de la api de libros
      useEffect(() => {
        consultaTres().then((response) => {
          setconsulta(response);
        });
        setReloadConsulta(false);
      }, [reloadConsulta]);

      console.log(consulta);

      const columns = [
        {
          text: "Trabajadores con m치s de 5 a침os de experiencia: ",
          dataField: "consultatres",
        },
      ];

      return (
        <BootstrapTable
          bootstrap4
          keyField="consultatres"
          data={consulta}
          columns={columns}
          pagination={paginationFactory({ sizePerPage: 5 })}
          striped
          hover
          condensed
        />
      );

    },
  });
  return (
    <Form className="login-form" onSubmit={formik.handleSubmit}>
      <h2>Ingresa el ID de la casa a consultar.</h2>
      <Form.Control
        type="number"
        placeholder="ID Casa"
        name="id"
        value={formik.values.id}
        onChange={formik.handleChange}
        error={formik.errors.id && true}
      />
      <Button type="submit" className="btn-submit">
        Confirmar
      </Button>
      {error && <p className="submit-error">{error}</p>}
    </Form>
  );
}

function initialValues() {
  return {
    id: "",
  };
}
